vec4 lin2srgb(vec4 c) { return pow(c,vec4(1.0/2.4)); }
vec4 srgb2lin(vec4 c) { return pow(c,vec4(2.4)); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspect = iResolution.x / iResolution.y;
  	vec2 uv = fragCoord / iResolution.xy;

  	vec2 a = floor(fragCoord); 
  	vec2 b = fract(fragCoord);

    bool checker = mod(a.x + a.y, 2.0 ) > 0.0;
    bool outside = distance(uv, vec2(0.5, 0.5)) > 0.4;

    if( checker && outside )
    {
        discard;
    }

    vec4 color = texture(iChannel0, fragCoord.xy / iResolution.xy);
    color = srgb2lin( color );

    if ( outside ) 
        color *= 2.0;


    fragColor = lin2srgb(color);
}
