// variant of https://shadertoy.com/view/ttscRN
// variant of https://shadertoy.com/view/wllcRN

void mainImage( out vec4 O, vec2 U )
{
    vec2  R = iResolution.xy;
    ivec2 I = ivec2(U+60.*iTime);
    int t = int(iTime) % 3, 
        n = int(U.x/R.x*4.),
        i =   t==1 ? I.x ^ I.y
            : t==2 ? I.x | I.y
            :        I.x & I.y;
                 //  I.x * I.y;

    float v = pow( float(i), floor(U.x/R.x*4.*5.)/5. );

    O = vec4( fract( v / 256.) );

    if ( int(U.x)*20 % int(R.x) < 20 ) O.r++; // red separator
}
