void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy;
    vec2 r = iResolution.xy;
    uv = 100000.*abs(cos(iTime)*sin(iTime))*(uv+uv-r)/r.y;
    uv.y = -uv.x;
    uv/=1.+uv.y/100.;
    uv.y -= iTime;
    uv=abs(fract(uv)-.05);
    uv = .05/sqrt(uv);
    //uv = vec2(cos(time)*uv.x-uv.y*sin(time),uv.x*sin(time)+uv.y*cos(time));
    mat2 roth = mat2(vec2(cos(iTime),-sin(iTime)),vec2(sin(iTime),cos(iTime)));
    uv*=roth;
    fragColor=(uv.x+uv.y)*vec4(1.,.6+.4*sin(iTime*.25),0.,0.)*.8;
    fragColor+=(uv.x+uv.y)*vec4(.8,.4,0.,0.)*.8;

}
