void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

   float battery=iMouse.x/iResolution.x;
    float g;
	vec2 uv = fragCoord.xy / iResolution.xy;
    g = 0.0;
    if(uv.x >0.5){
        if(uv.y>0.5){
			g = uv.x*uv.x+uv.y*uv.y-(battery*(2.0*battery));
        }
        if(uv.y<0.5){
         	g = uv.x*uv.x+(1.0-uv.y)*(1.0-uv.y)-(battery*(2.0*battery));
        }
    }
     if(uv.x <0.5){
        if(uv.y>0.5){
			g = (1.0-uv.x)*(1.0-uv.x)+uv.y*uv.y-(battery*(2.0*battery));
        }
        if(uv.y<0.5){
         	g = (1.0-uv.x)*(1.0-uv.x)+(1.0-uv.y)*(1.0-uv.y)-(battery*(2.0*battery));
        }
    }
    if(battery == 0.1){
        g=1.0;
            }

	fragColor = vec4(1.0-g,battery-0.4,g,1.0);

}
