void mainImage( out vec4 o, in vec2 U )
{
    o=vec4(0);//Required on Safari
    vec3 u=normalize(vec3(2.*U-iResolution.xy,iResolution.y));
    for(int i=0;i<6;i++){
        u.x+=sin(u.z+iTime*.1);
        u.y+=cos(u.x+iTime*.1);
        o=max(o*.9,cos(3.*dot(u,u)*vec4(.3,.1,.2,0)));
    }
}
