#define PI 3.14159265358979323846
vec2 rotate2D(vec2 _st, float _angle){
    _st -= 0.5;
    _st =  mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle)) * _st;
    _st += 0.5;
    return _st;
}

vec2 tile(vec2 _st, float _zoom){
    _st *= _zoom;
    return fract(_st);
}

float strange(vec2 _st,float width){
    float uv ;
    if(abs(_st.y-0.5) >0.1){
    	uv = step(0.5-width,_st.x) - step(0.5+width,_st.x) ;
    }
    if(abs(_st.x-0.5) >0.1){
   	uv += step(0.5-width,_st.y) - step(0.5+width,_st.y);
    }
    return uv;
}

float box(vec2 _st, vec2 _size, float _smoothEdges){
    _size = vec2(0.5)-_size*0.5;
    vec2 aa = vec2(_smoothEdges*0.5);
    vec2 uv = smoothstep(_size,_size+aa,_st);
    uv *= smoothstep(_size,_size+aa,vec2(1.0)-_st);
    return uv.x*uv.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 st = fragCoord/iResolution.xy;
    vec3 color = vec3(0.0);

    st = tile(st,4.);

    color = vec3(strange(st,0.015));
    st = rotate2D(st,PI*0.25);

    color -= vec3(box(st,vec2(0.15),0.03));
    color += vec3(box(st,vec2(0.23),0.05));

    fragColor = vec4(color,1.0);
}
