// /!\ move everything fast and flashy #define time tan(iTime*16.)
#define time iTime
#define clock time/5.
#define ttime floor(time) + pow(fract(time),sin(iTime*1.333)*.5)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv =  ( fragCoord -.5* iResolution.xy ) / iResolution.y;
    vec2 uuv = uv;
    uv.x += tan(abs(uv.x)*5.);

    float d = fract(10.*uv.x+clock);
    d = smoothstep(0.2,0.10,d);

    float p = tan(uv.x+time);
    vec3 col = mix(vec3(0.1,0.1,0.1),vec3(p*0.1+step(.1,abs(cos(clock*2.))*uv.x),p*0.9,0.1+step(1.,sin(clock)*sin(clock)*abs(uv.x))),d);

    fragColor = vec4(
        col,
        d*sin(ttime+1.-floor(length(floor(uuv*10.)))));
}
