float frame(vec2 pos,float size, float frameSize)
{
     // 左と下の内側
    vec2 inBl = step(vec2(size - frameSize * 0.5),pos);
    // 左と下の外側
    vec2 bl2 = 1. - step(vec2(size + frameSize * 0.5),pos);
    float outBl = bl2.x + bl2.y;

    // 上と右の内側
    vec2 inTr = step(vec2(size  - frameSize * 0.5 ) ,1. - pos);
    // 上と右の外側
    vec2 tr2 = 1.-  step(vec2(size  + frameSize * 0.5) ,1. - pos);
    float outTr = tr2.x + tr2.y;

    // 上の4つを組み合わせる
	return 1. - (inBl.x * inBl.y * outBl * (inTr.x * inTr.y)) - (inTr.x * inTr.y * outTr * (inBl.x * inBl.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

	vec3 col = vec3(frame(uv, 0.2, 0.1));


    // Output to screen
    fragColor = vec4(col,1.0);
}
