void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv;

    uv = (vec2(iResolution.x / 2.0, iResolution.y / 2.0) - fragCoord.xy) / (20.1);

    float s = 1.0;
	float b = 2./3. * uv.y / s;
	float r = (sqrt(3.)/3. * uv.x - uv.y/3. ) / s;
	float g = -(sqrt(3.)/3. * uv.x + uv.y/3. ) / s;  


    float v = 25.;
    float st = 5.;
    float t = 5.5;

    float triSize = 3.14159;
    float tri = sign(mod(floor(r / triSize) + floor(g / triSize) + floor(b / triSize), 2.) * 2. - 1.) ;

    float value = cos(r + sin(iTime / 2.) * 3.14159) * cos(g + sin(iTime) * 3.14159) * cos(b + sin(iTime / 3.) * 3.14159) * tri * ((st) + t);
    float color = sin(value) * 3.0;


    float low = abs(color);
    float med = abs(color) - 1.0;
    float high = abs(color) - 2.5;
    if(color > 0.0) {
	  fragColor = vec4(med, high, med,1.0);
    } else {
      fragColor = vec4(min(0.5, med / 2.0), high, med,1.0);
    }
}
