#ifdef GL_ES
precision mediump float;
#endif

vec2 checkerboard(vec2 vectorA, vec2 vectorB, vec2 value){
    return floor(vectorA / value) - step(vectorB, value);
}

float colPlay(float col, float valA, float valB){
    return pow(abs(sin(col)/valA), valB);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 st = fragCoord.xy / iResolution.xy;
    vec3 color = vec3(0.0);
    float pct;

    vec2 bl = checkerboard(vec2(sin(iTime)), vec2(cos(iTime)), st);
    pct = bl.x * bl.y;

    color = vec3(colPlay(pct, 2.0, 0.5), colPlay(pct, 0.5, 3.0), colPlay(pct, 3.0, 0.5));

    fragColor = vec4(color,1.0);
}
