float circ(vec2 uv, vec2 pos, float r, float blur){

    vec2 off = pos*r;
    off.x += sin(iTime*r*7.)*.02;
    off.y += cos(iTime*r*4.)*.04;

    float d = length(uv+off);//+sin(iTime*5.)*.01; // distance from center

    float coli = d;

    return smoothstep(r,r-blur,d);



}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5; //center it
    uv.x *= iResolution.x/iResolution.y;

    float t = .1;//basecolor

   int i=0;
   while(i<10){

        t += circ( uv,
        vec2(sin(float(i)*1.2),cos(float(i)*1.2)),
        float(i)*.04,
        float(i)*.02
        );

        i++;
    }


    vec3 col = vec3(t*.4)+.4*cos(iTime+uv.xyx+vec3(5,3,4))*t;   // Time varying pixel color

    // Output to screen
    fragColor = vec4(col,1.0);
}
