const float COLUMNS = 80.0;
vec3 WHITE = vec3(1.0,1.0,1.0) - 0.05;
vec3 BLACK = vec3(0.0,0.0,0.0) + 0.25;
vec3 GREEN = vec3(0.0,0.5,0.5) - 0.05;
vec3 ORANGE = vec3(0.75,0.5,0.0) + 0.05;


bool is_inside_region(float point, float initialHeight, int m, float blockSeparation, float width) {
    float centre;
    for(int i=0; i < m; i++) {
        centre = float(i)*blockSeparation + initialHeight;
        if (centre - width/2.0 <= point && point <= centre + width/2.0){
        return true;
        }
    }
    return false;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col;


    float column = floor(COLUMNS * uv.x);
    float m = 6.0;

    float y = m * COLUMNS * uv.y;
    // float q = floor(y / COLUMNS);
    // float y_ = y - q * COLUMNS;

    float width = 0.05 * COLUMNS * m;

    float initialHeight;


    if ( int(column) % 2 == 0 ) {
        col = WHITE;
        if ( int(column) % 4 == 0 ) {
            initialHeight = column;
            if (is_inside_region(y, initialHeight, int(m/2.0), 2.0 * COLUMNS, width)) {
                col = GREEN;
            }
        } else if ( int(column) % 4 == 2 ) {
            initialHeight = 2.0 * COLUMNS - column;
            if (is_inside_region(y, initialHeight, int(m/2.0), 2.0*COLUMNS, width)) {
                col = GREEN;
            }
        }
    } else {
       col = BLACK;
       if ( int(column) % 4 == 1) {
            initialHeight = COLUMNS + column;
            if (is_inside_region(y, initialHeight, int(m/2.0), 2.0*COLUMNS, width)) {
                col = ORANGE;
            }
        } else if ( int(column) % 4 == 3) {
            initialHeight = COLUMNS - column;
            if (is_inside_region(y, initialHeight, int(m/2.0), 2.0*COLUMNS, width)) {
                col = ORANGE;
            }
        }
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
