void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord -.5 * iResolution.xy) / iResolution.y;

    float col = 0.;
    float period = 30.;
    float one_pix = 1.5/iResolution.y ; // <- How to fix aliasing from low to high amplitude ?

    uv.x =mod((uv.x + iTime*.2), 1.); // <- How to loop this correctly (if you comment this line you'll see the loop i'm looking for) ?
    float mult = 1. - length(uv);

	col = abs(uv.y + sin(uv.x * period) *1. * mult) ;
   	col = smoothstep(one_pix * 1., 0., col);

    fragColor = vec4(vec3(col),1.0);
}
