 void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float wave_uv_y = fragCoord.y / 12.0 + sin(fragCoord.x / 10.0) * 0.5;
    float wave_cos = cos(fragCoord.x / 10.0);

    float wave_fract = fract(wave_uv_y);

    float wave = 1.0 - clamp(abs(wave_fract * 2.0 - 1.0) * 6.0 - 1.0, 0.0, 1.0);
    wave *= mod(wave_uv_y, 4.0) < 2.0 ? 0.0 : 1.0;

    float wave_color_offset = (cos(iTime * 0.6) * 2.0) + (floor(wave_uv_y * 0.25) * acos(-1.0) * 0.5);
    float wave_color = sin(fragCoord.x / 10.0 + wave_color_offset) > 0.0 ? 0.65 : 0.35;

    vec2 uv = fragCoord/iResolution.xx;
    float offset = sin(iTime * 0.5) * 0.35;
    if (iMouse.z > 0.0)
        offset = 0.5 - iMouse.x / iResolution.x;

    float diag = uv.x - 0.5 + offset - (uv.y - 0.5*iResolution.y/iResolution.x) * 0.5;
    float back_color = 0.5;
    back_color = mix(1.0, back_color, clamp((diag + 0.35) * iResolution.x * 0.75, 0.0, 1.0));
    back_color = mix(back_color, 0.0, clamp((diag - 0.35) * iResolution.x * 0.75, 0.0, 1.0));

    float col = mix(back_color, wave_color, wave);

    fragColor = vec4(col, col, col, 1.0);
}
