// Sandbox based on IQ's procedural pallete example:
// Pallette Sandbox based on IQ's example:
//
// https://www.shadertoy.com/view/ll2GD3
// https://iquilezles.org/articles/palettes
// The MIT License
// Copyright Â© 2015 Inigo Quilez

// === cut/paste start here ===
//      A Component  R     G     B   (Y shift)
#define pal_a vec3( 0.50, 0.50, 0.50 )  
//      B Component  R     G     B   (amplitude)
#define pal_b vec3( 0.50, 0.50, 0.50 )  
//      C Component  R     G     B   (period)
#define pal_c vec3( 1.00, 0.70, 0.40 )  
//      D Component  R     G     B   (phase)
#define pal_d vec3( 0.00, 0.15, 0.20 )  

vec3 pal( in float t )
{
    return pal_a + pal_b*cos( 6.28318*(pal_c*t+pal_d) );
}
// === cut/paste end here ===

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;
    float thickness = .015;

    // Animate
    p.x += 0.05*iTime;

    // Default color
    vec3 col = vec3(0);

    // Band
    float f = fract(p.y*4.0);

    // Curve for B
    col = vec3(0, 0, smoothstep(thickness+.01, thickness, abs(pal(p.x).z-f)));

    // Curve for G
    if( p.y>(1.0/4.0) ) {
	    col = vec3(0, smoothstep(thickness+.01, thickness, abs(pal(p.x).y-f)), 0);
    }
    // Curve for R
    if( p.y>(2.0/4.0) ) {
	    col = vec3(smoothstep(thickness+.01, thickness, abs(pal(p.x).x-f)), 0, 0);
    }

    // Compute palette color
    if( p.y>(3.0/4.0) ) col = pal( p.x );

    // Borders
    col = mix(col, vec3(.5), vec3(step( .5-thickness, abs(f-0.5))));

	fragColor = vec4( col, 1.0 );
}
