#define PI 3.14159

float aa_smoothstep(float lines, float v) {
        float dl = fwidth(lines);
        return smoothstep(v - dl, v + dl, lines);
}

vec2 aa_smoothstep(vec2 lines, float v) {
        vec2 dl = fwidth(lines);
        return smoothstep(v - dl, v + dl, lines);
}

float half_plane(vec2 uv, float t, float radius) {
    vec2 st = uv * mat2( cos(t), sin(t),
                        -sin(t), cos(t));

    return st.y + radius;
}

float flexagon(vec2 uv, float t, float radius) {
    //t = 0.;
    float d = (half_plane(uv, 0.*PI/3.5 + t, radius + sin(t*1.889)*0.1 + 0.1));
    d = min(d, half_plane(uv, 1.*PI/3.5 + t, radius + sin(t*1.230)*0.1 + 0.1));
    d = min(d, half_plane(uv, 2.*PI/3.5 + t, radius + sin(t*1.965)*0.1 + 0.1));
    d = min(d, half_plane(uv, 3.*PI/3.5 + t, radius + sin(t*1.431)*0.1 + 0.1));
    d = min(d, half_plane(uv, 4.*PI/3.5 + t, radius + sin(t*1.728)*0.1 + 0.1));
    d = min(d, half_plane(uv, 5.*PI/3.5 + t, radius + sin(t*1.354)*0.1 + 0.1));
    d = min(d, half_plane(uv, 6.*PI/3.5 + t, radius + sin(t*1.194)*0.1 + 0.1));

    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (2.* fragCoord - iResolution.xy) / iResolution.y;
    vec3 color = vec3(0.8);

    float t = iTime * 0.4 + 900.;
    float d = flexagon(uv, t, 0.5);
    float e = exp(d*7.)*1.;
    float b = abs(fract(e - fract(t))*2. - 1.);
    float mask = aa_smoothstep(b, 0.8);
    //mask *= smoothstep(0.25, 0.15, d);
    mask = max(mask, aa_smoothstep(d, 0.3));

    // Output to screen
    fragColor = vec4(mix(vec3(0.2), color, mask), 1.0);
}
