mat2 r(float a){ float c= cos(a),s=sin(a);
return mat2(c,-s,s,c);}
float fig(vec2 uv) {
   uv*=5.;
   uv*=r(iTime*.33 + cos(length(uv.y-uv.x)));
    return 0.1/smoothstep(0.1,0.9,abs(uv.x*4.)+sin(iTime*.5)*+pow(length(uv),2.));
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
     vec2 uv = (fragCoord-.5*iResolution.xy) / iResolution.y;

     vec3 col = vec3(0.);

     col = vec3( fig(uv) + fig(uv*r(3.141/2.))  );
     vec2 cc = uv*r(-iTime);
     col= mix(vec3(.0,.0,.0),vec3(.9 * abs(atan(cc.x,cc.y))/3.141592,.2,.9),col);
    fragColor = vec4(col,1.0);
}
