// Draw a linear grey gradient from right (darker) to left (brighter)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Divide the actual x distance by the x resolution
    // We ignore the y value since the linear gradient is proportional 
    //  only to the x values
    float c = (iResolution.x - fragCoord.x) / iResolution.x;

    fragColor = vec4(c,c,c,1.0);
}
