#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,r=iResolution,
    d=normalize(vec3((C-.5*r.xy)/r.y,1));  
    for(float i=1.,g=0.,e,s,a;
        ++i<99.;
        a=cos(i*i/80.),O.rgb+=mix(vec3(1),H(log(s)/5.),.5)*a*a/e/2e4
    )
    {
        p=g*d-vec3(-.6,.2,2);
        p=R(p,normalize(vec3(1,1,1)),iTime*.1);
    s=3.;
    for(int i=0;i++<5;p=abs(p)*e)
    p=vec3(6,4,2)-abs(p-vec3(9,4,2)),
    s*=e=8./clamp(dot(p,p),.1,8.);
  g+=e=min(length(p.xz),p.y)/s+.001;}
}
