#define NUM_SEGMENTS 20.
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(0.);
    uv.y *= 3.;
    uv.y -= 0.7;
    for(float j = 0.; j < NUM_SEGMENTS; j+=1.){
    	for(float i = 0.; i < 10.; i += 1.){
        	float x = uv.y;
        	x += 0.5;
    		x +=  1.-sin(iTime* 3.+ uv.x * j) * 0.4* j * atan(i + j )*0.5;
  			col += (abs(0.01/(1.- x))*.3) * vec3(1.- j/NUM_SEGMENTS,i/NUM_SEGMENTS, i/NUM_SEGMENTS);
    	}
    }

    fragColor = vec4(col,0.);
}
