void mainImage( out vec4 c, in vec2 a )
{
    vec3 o = vec3(sin(iTime),2.3,cos(iTime));
    vec3 w = normalize(-o);
    vec3 u = normalize(cross(w,vec3(0,1,0)));
	vec3 d = mat3(u,cross(u,w),w)*
        normalize(vec3((2.*a-iResolution.xy)/max(iResolution.x,iResolution.y),.25));
    float t = 0., e = 1.e-4, m = 5., h = 1.;
    while (t < m && h > e)
    {
        h = length(o+d*t+vec3(0,0,1))-2.;
        t = min(m,t+h);
    }
    c = vec4(.2*t);
}
