void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord.xy/iResolution.xy;
    vec4 l_color = texture(iChannel1, uv);
    vec4 f_color = texture(iChannel0, uv);
    f_color.rgb = f_color.rgb * l_color.a;
    fragColor = f_color;

}

#define PI 3.14159

float getLightColor(vec2 uv, float p_ratio, float plus, float minus){
    float color = 0.;
    float angle = atan(uv.x, uv.y);
    float alpha_r = smoothstep(1./p_ratio *PI - 0.1, 1./p_ratio *PI , angle);
    float alpha_l = smoothstep(-1./p_ratio *PI, -1./p_ratio *PI + 0.1, angle);
    float alpha = alpha_l * (1. - alpha_r);
    if (alpha == 0.) {alpha = 0.02;}

    float circle = plus/sqrt(minus * length(uv));
    color = circle * circle * alpha + 0.05;
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord.xy/iResolution.xy;
    uv.x = uv.x * iResolution.x/iResolution.y * 0.75;
    vec2 mouse = iMouse.xy/iResolution.xy;
    mouse.x = mouse.x * iResolution.x/iResolution.y * 0.75;

    if(mouse.x == 0. && mouse.y == 0.){
        mouse.x = 0.65;
        mouse.y = 0.1;
    }

    uv -= mouse;
    float outColorA = getLightColor(uv, 3.5, 0.2, 0.25);
    float insideColorA = getLightColor(uv, 5., 0.15, 0.25);
 	float colorA = outColorA + insideColorA;
    fragColor = vec4(colorA);
}
