void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     vec3 c;
    vec2 uv = fragCoord/iResolution.xy;

	vec2 pixelSize = vec2(12.0,12.);

    vec3 src = texture(iChannel0, uv).rgb;

	vec3 sum;


   for(int a=0;a<int(pixelSize.x);a++)

    {
         for(int b=0;b<int(pixelSize.y);b++)
         {
          c=texture(iChannel0, (fragCoord-mod(fragCoord,pixelSize)+vec2(vec2(a,b)))/iResolution.xy).rgb;

             sum+=c;

         }



    }
        sum/=pixelSize.x*pixelSize.y;




             	fragColor.rgb=sum;





    if (uv.x<iMouse.z)
    {
   fragColor.rgb=src.rgb;

    }

}
