void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = fragCoord - iResolution.xy / 2.0;
    coord /= iResolution.x;

    vec2 offset = round(coord / 0.025) * 0.025;
    vec4 col = vec4(0.0);

    for(int i = 0; i < 3; i++) {
        float t = iTime + float(i)*2.1 + (abs(coord.x) + abs(coord.y))*10.0;
        float r = (pow(sin(t), 3.0) + 1.0) * 0.02;

        if(length(coord - offset) < r) {
           col[i] = 1.0;
        }
    }

    fragColor = col;
}
