void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Output to screen
    vec2 xy = fragCoord.xy;

    float posX = xy.x / iResolution.x;
    float posY = xy.y / iResolution.y;

	float outp = (posX + posY)/2.0;

    vec4 col = vec4(0,0,0,1.0);
    //col.r = (xy.y + xy.x)/2.0;
    col.b = outp;

    fragColor = col;
}
