vec4 char(vec2 p, int C) 
{
	if (p.x<0.|| p.x>1. || p.y<0.|| p.y>1.) return vec4(0,0,0,1e5);
    return textureGrad(iChannel3,p/16.+fract(vec2(C,15-C/16)/16.),dFdx(p/16.),dFdy(p/16.));
}

void mainImage(out vec4 O, in vec2 f) 
{
    vec2 U = (f * 4. - iResolution.xy) / min(iResolution.y, iResolution.x);
    U.x += 1.; U.y -= .5;
    for (int i = 0; i < 8; ++i)
        O += char(U, int[](83, 111, 117, 110, 100, 32, 105, 110)[i]).x, U.x -= .69;



    O.bg -= mainSound( 0,iTime);
}

// nabr
// https://www.shadertoy.com/view/ws2Szz
