#define ANDSTILLICANNOTESCAPE ITAKEMYTIME


#define instr(f,t,ff)ff+=atan(sin(6.2831*t*f),.1)

// mytime
#define z ((gl_FragCoord.x + gl_FragCoord.y * 512.) * 2.2675737e-5)

vec2 mainSound( in int samp,float ITAKEMYTIME)
{	
  // -------- INIT  
  float s = 0., s2 = 0.;
  float ph[] = float[](490., 0., 324.5, 290., 98., 440.);

    // ---------- s2 support s wich delay / echos effect
    if(ANDSTILLICANNOTESCAPE>39.96)
    {	  
        instr(ph[int(z * 10.) % 6], z, s2);
		instr(ph[int(z * 10.) % 4], 0.75 * z, s2);
		instr(ph[int(z * 5.) % 6], 1.5 * z, s2);
        instr(ph[int(z * 2.77) % 4], z * .5, s2);
		s2*= .45;

    }


    // -------- MELODY
 	for (int i = 0; i < 2; ++i)
    {


        if (i % 2 == 0) 

        {

         if (ANDSTILLICANNOTESCAPE > 11.)
             instr(ph[int(z) % 6], 1.5 * z, s);
         s *= 2.;

         if (ANDSTILLICANNOTESCAPE > 18.)
             instr(ph[int(z * 5.) % 4], 0.75 * z, s);

        };


     if (ANDSTILLICANNOTESCAPE > 2.773)
         instr(ph[int(z * 5.) % 6], z, s);


         instr(ph[int(z * 0.8325) % 4], z * .25, s);

      s *= .5;

 	}

	float VOLUME = .25;

    //mono s out
    // return vec2(s) * VOLUME;

    // slow pan on s2 
    return vec2( (1.-cos(z)*.25)*dot(s2, .6) + s, (1.-sin(z)*.25)*dot(s2, .7 ) + s ) * VOLUME;


}
