// Value noise
float noise(in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = rand(i);
    float b = rand(i + vec2(1.0, 0.0));
    float c = rand(i + vec2(0.0, 1.0));
    float d = rand(i + vec2(1.0, 1.0));

    // Smooth Interpolation

    // Cubic Hermine Curve.  Same as SmoothStep()
    vec2 u = f*f*(3.0-2.0*f);
    // u = smoothstep(0.,1.,f);

    // Mix 4 coorners porcentages
    return mix(a, b, u.x) + 
            (c - a)* u.y * (1.0 - u.x) + 
            (d - b) * u.x * u.y;
}

// Gradient noise
vec2 hash( vec2 x )  // replace this by something better
{
    const vec2 k = vec2( 0.3183099, 0.3678794 );
    x = x*k + k.yx;
    return -1.0 + 2.0*fract( 16.0 * k*fract( x.x*x.y*(x.x+x.y)) );
}

float gnoise( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( hash( i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ), 
                     dot( hash( i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( hash( i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ), 
                     dot( hash( i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}

// Simplex noise
vec3 mod289(vec3 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
vec2 mod289(vec2 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
vec3 permute(vec3 x) { return mod289(((x*34.0)+1.0)*x); }

float snoise(vec2 v) {
    const vec4 C = vec4(0.211324865405187,  // (3.0-sqrt(3.0))/6.0
                        0.366025403784439,  // 0.5*(sqrt(3.0)-1.0)
                        -0.577350269189626,  // -1.0 + 2.0 * C.x
                        0.024390243902439); // 1.0 / 41.0
    vec2 i  = floor(v + dot(v, C.yy) );
    vec2 x0 = v -   i + dot(i, C.xx);
    vec2 i1;
    i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
    vec4 x12 = x0.xyxy + C.xxzz;
    x12.xy -= i1;
    i = mod289(i); // Avoid truncation effects in permutation
    vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
        + i.x + vec3(0.0, i1.x, 1.0 ));

    vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy), dot(x12.zw,x12.zw)), 0.0);
    m = m*m ;
    m = m*m ;
    vec3 x = 2.0 * fract(p * C.www) - 1.0;
    vec3 h = abs(x) - 0.5;
    vec3 ox = floor(x + 0.5);
    vec3 a0 = x - ox;
    m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );
    vec3 g;
    g.x  = a0.x  * x0.x  + h.x  * x0.y;
    g.yz = a0.yz * x12.xz + h.yz * x12.yw;
    return 130.0 * dot(m, g);
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     //Colors in vec3
    vec3 red = vec3(0.8 ,0.0 ,0.0);
    vec3 white = vec3(1.0, 1.0, 1.0);
    vec3 yellow = vec3(0.9, 0.9, 0.3);
    vec3 blue = vec3(0.5, 0.8, 0.9);
    vec3 black = vec3(0.0, 0.0, 0.0);
    vec3 green = vec3(0.0, 1.0, 0.0);


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv *= 4.0;
    float value = gnoise(uv + (sin(iTime) * 0.005));

    //music shiz
    float val = noise(uv + (iTime) * (iTime * 0.001) - (uv.x*10.));

    //val = val + gnoise(uv * snoise(uv * rand(uv)) * noise(uv * rand(uv)) + iTime + rand(uv*iTime));
    //val = val * noise(uv + (iTime) * (iTime * 0.005) - abs(uv.x*clamp(sin(iTime), 0.0, 1.0)) * 100.0);
    val = val + snoise(uv + (iTime * 1.0) + vec2(0.0, -iTime*2.0));


    //waves
    val = step(-0.1*val, uv.y) - step(2.5*val, uv.y);


    vec3 lampLight = abs(vec3(
        				  clamp(abs(sin(iTime)),0.3 , 0.8 ),
                          clamp(abs(cos(iTime)),0.4 , 0.9 ), 
                          clamp(abs(sin(iTime)),0.4 , 1.0 ))
                        	 );


    vec3 Val = mix(lampLight, black, val);
    Val = mix(lampLight, black, Val);

    float ratio = iResolution.x/iResolution.y;
    uv.x *= ratio * 0.25;
    uv.y += 0.5;

    vec2 shapePos = vec2((0.5 * ratio) /*plus minus something to adjust Pos*/, 0.5);
    float dist = distance(shapePos , uv) * 2.50;


    uv -= shapePos;

    float angle = atan(uv.y, uv.x);

    float radius = sin(4.0 * angle);
    radius = smoothstep(-0.75 , 1.0,  cos(angle * 8.0 + iTime * 15.0)  ) * 0.2 + 0.4;

    float radius2 =  1.0 /  abs(cos(angle * 10000.0 + iTime) * sin(angle * 7.0 + iTime)) * 0.5 + 0.1;

    value =  step(radius + radius2 * clamp(sin(iTime * 0.0)+1.2,0.1, 3.0), dist);


    // Output to screen
    fragColor = vec4(value * Val, 1.0);

}
