#define DARK_BLUE 	0x6169AE
#define PURPLE 		0x5C3489
#define CYAN 		0x83C9E3
#define ORANGE 		0xE86B62

#define C_PURPLE 	0x694098
#define C_RED 		0xE0323F
#define C_BLUE		0x3EB4DD
#define C_DGREEN	0x3EC677
#define C_ORANGE	0xF4AD52
#define C_LGREEN	0xB3DB58

void box(inout vec3 col, vec2 coord, vec4 bounds, vec3 color) {
    bool bb = 	all(greaterThanEqual(coord, bounds.xy)) &&
        		all(lessThan(coord, bounds.zw));
    col=bb?color:col;
}

float random(vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898,78.233)))*43758.5453123);
}

vec3 hex2rgb(int hex) {
	return vec3(hex>>16&0xFF,hex>>8&0xFF,hex&0xFF)/255.;
}

void base(inout vec3 col, vec2 ndc) {
    box(col, ndc, vec4(-0.25,-0.25,0.25,0.25), vec3(1.));

    box(col, ndc, vec4(-0.275,0.25,-0.25,0.275), hex2rgb(C_PURPLE));

    box(col, ndc, vec4(-0.275,0.25,-0.25,0.275)+vec4(0.025,0.,0.025,0.), 	hex2rgb(C_RED));
    box(col, ndc, vec4(-0.275,0.25,-0.25,0.275)+vec4(0.,-0.025,0.,-0.025), 	hex2rgb(C_RED));

    box(col, ndc, vec4(-0.275,0.25,-0.25,0.275)+vec4(0.05,0.,0.05,0.), 		hex2rgb(C_BLUE));
    box(col, ndc, vec4(-0.275,0.25,-0.25,0.275)+vec4(0.,-0.05,0.,-0.05), 	hex2rgb(C_BLUE));

    box(col, ndc, vec4(-0.275,0.25,-0.25,0.275)+vec4(0.075,0.,0.075,0.), 	hex2rgb(C_DGREEN));
    box(col, ndc, vec4(-0.275,0.25,-0.25,0.275)+vec4(0.,-0.075,0.,-0.075), 	hex2rgb(C_DGREEN));

    box(col, ndc, vec4(-0.275,0.25,-0.25,0.275)+vec4(0.1,0.,0.1,0.), 		hex2rgb(C_ORANGE));
    box(col, ndc, vec4(-0.275,0.25,-0.25,0.275)+vec4(0.,-0.1,0.,-0.1), 		hex2rgb(C_ORANGE));

    box(col, ndc, vec4(-0.275,0.25,-0.25,0.275)+vec4(0.125,0.,0.125,0.), 	hex2rgb(C_LGREEN));
    box(col, ndc, vec4(-0.275,0.25,-0.25,0.275)+vec4(0.,-0.125,0.,-0.125), 	hex2rgb(C_LGREEN));   
}

void pattern0(inout vec3 col, vec2 ndc) {
    for(float j=1.; j<9.; j+=1.) {
        box(col, ndc, vec4(-0.20,-0.25,-0.15,-0.20)+vec4(0.,j*0.05,0.,j*0.05), hex2rgb(ORANGE));
        box(col, ndc, vec4( 0.15,-0.25, 0.20,-0.20)+vec4(0.,j*0.05,0.,j*0.05), hex2rgb(ORANGE));
    }

    for(float i=1.; i<5.; i+=1.) {
        if(ndc.y > -0.2 && ndc.y < 0.2) {
            vec2 ndcmod = ndc;
            ndcmod.y = mod(ndcmod.y-0.5*iTime, 0.2) - 0.2;

            vec4 coord = vec4(-0.15+i*0.05, 0.00-i*0.05,
                              -0.10+i*0.05, 0.05-i*0.05);

            if(coord.y >= -0.2 && coord.y <= 0.2) {
                box(col, ndcmod, coord, hex2rgb(ORANGE));
            }

            if(i==1.) {
                coord += vec4(0.2,0.0,0.2,0.0);
                box(col, ndcmod, coord, hex2rgb(ORANGE));
            } else if(i==4.) {
                coord -= vec4(0.2,0.0,0.2,0.0);
                box(col, ndcmod, coord, hex2rgb(ORANGE));
            }
        }
    }
}


void mainImage(out vec4 O, in vec2 I) {
    vec2 uv = I/iResolution.xy;
    vec2 ndc = I/iResolution.yy - vec2(16./9.,1.)/2.;


    float mixval = random(uv);

    vec3 col = mix(hex2rgb(PURPLE), hex2rgb(DARK_BLUE), uv.y);

    base(col, ndc);
    pattern0(col, ndc);


	O = vec4(col, 1.);
}
