#define TAU 6.28318531

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = (fragCoord * 2.0 - iResolution.xy) / iResolution.x;
    vec2 m = iMouse.xy/iResolution.xy;

    float angle = mod(atan(coord.y, coord.x), TAU);
    float len = length(coord);

    vec3 col = vec3(0.0);

    for(int i = 0; i < 3; i++) {
        float o = float(i)/3.0;
        float s = sin(angle + len*(4.0+m.y*6.0) - iTime*2.0 + o*TAU*(1.0-m.x)) * 0.5+0.5;
        float d = 0.1 + (sin(coord.x*80.0) * 0.5+0.5)*0.4*s - abs(fract(coord.y*10.0) - 0.5) * 2.0;
        float v = smoothstep(-fwidth(d)/2.0, fwidth(d)/2.0, d);

        col[i] = v;
    }


    fragColor = vec4(col, 1.0);
}
