void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 center = vec2(0.5, 0.5);    
    float intensity = 1.5;

    vec2 normalized = uv - center;
    float dist = 1.0-sqrt(normalized.x*normalized.x+normalized.y*normalized.y);
    normalized *= dist;    

    fragColor = texture(iChannel0, normalized+center)*((dist*intensity));
 }
