void mainImage(out vec4 O, vec2 C)
{
    O-=O;
	for(float g,e,i=0.;i<30.;i++)
    {
        vec3 p=vec3(C.xy/iResolution.y-.6,1.)*g;
        p.xy+=sin(iTime)*.1;
        g+=e=length(vec2(mod(length(p.xy),.4)-.2,mod(p.z+iTime*2.,.8)-.4))-.01;
        O+=cos(vec4(6,2,9,0)+atan(p.x,p.y)*16.)*((e<.01)?1./i:0.);
    }
}

