#define center vec2(iResolution.x / 2., iResolution.y / 2.)


float eps = 1.0;

float radius = 200.;

bool isHit(vec2 inverted) {    
    if (abs(dot(inverted, vec2(1., 1.0)) + 100.) < eps ) {
    	return true;
    }

    if (abs(length(inverted + vec2(30., 50.)) - 30.) < eps ) {
    	return true;
    }

    if (abs(abs(inverted.x - iMouse.x + center.x) + abs(inverted.y - iMouse.y+ center.y) - 30.) < eps ) {
    	return true;
    }

    return false;
}

//read about inversion here http://whistleralley.com/inversion/inversion.htm
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 from_center = fragCoord - center;


    radius = iMouse.y + 10. + sin(iTime) * 10.;
    vec2 inverted = from_center;
    inverted = from_center * radius * radius / length(from_center) / length(from_center); 


    fragColor = vec4(0.,0.,0.,1.0);


    if (length(from_center) < 5.) {
    	fragColor = vec4(0.,0.,1.,1.0);
    }
    if (abs(length(from_center) - radius) < eps ) {
    	fragColor = vec4(0.,1.,0.,1.0);
    }

    fragColor = texture(iChannel0, (inverted + center) / iResolution.x);

}
