void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //vec2 uv = fragCoord/300.0;
    vec2 uv = fragCoord.xy/iResolution.xy;
    uv.x-=0.20;
    uv.x*=iResolution.x/iResolution.y;
    //vec3 a;
    float pi=3.14159;
    float rad=sqrt(pow(uv.x-0.5,2.0)+pow(uv.y-0.5,2.0)); //radius
    float o=atan((uv.y-0.5)/(uv.x-0.5));               //angle

    //o=o/(2.0*pi);
    //if (uv.x<0.5)
    //    o=(o+0.5);
    //o=o-iTime;
    //o=mod(o,1.0);

    //a=a*2.0/sqrt(2.0);
    //if (a.r>=0.999)//test
    //    a.r=0.0;
    //a.g=o;
    //a.b=1.0-a.r;

    //a=a+r;

    //rad=rad*2.0/sqrt(2.0); ///
    rad*=0.3;

    vec3 r;//rainbow

    vec2 x;
    x.x=atan(rad*pow(iTime*0.2+1.0,2.0)*5.0)-pow(iTime*0.2+1.0,2.0)*1.0;
    //x.x=(a.r+a.g+a.b)/3.0;//hack

    //x.x=x.x/iResolution.x*4.0;

    //x.x=x.x;//-iTime;
    r.r=sin(x.x*PI*1.0);
    r.g=sin(x.x*PI*1.0+PI/3.0);
    r.b=sin(x.x*PI+PI*2.0/3.0);

    //r=r*r;
    //r=abs(r);
    r=pow(abs(r),vec3(0.0001));
    //r.g=pow(r.g,0.0001);
    //r.b=pow(r.b,0.0001);

    r=r-min(min(r.r,r.g),r.b);
    r=r/max(max(r.r,r.g),r.b);



    fragColor = vec4(r,1.0);
    //fragColor = vec4(a,1.0);
}
