void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv-= 0.5;

    uv.x*=(iResolution.x/iResolution.y)+0.2*sin(iTime); // Make it adjust in xscale over time.

    float d = length(uv); // get length from center(0.5,0.5)
    vec3 c = vec3(0.0);
    float r = 0.5+0.1*sin(iTime); // radius of circle

    c=vec3(smoothstep(r, r-0.005, d))*vec3(0.5+0.5*cos(iTime+uv.xyx+vec3(0,2,4)));

    // Time varying pixel color
    vec3 col = vec3(c);

    // Output to screen

    fragColor = vec4(col,1.0);
}
