#define T iTime
float C,S;
#define rot(a) mat2(C=cos(a),S=sin(a),-S,C)
float map(vec3 q){
  float d=10e4;
  for(float f=0.;f<10.;++f){//  количество червей 10
   vec3 p=q;
   float ri=mod(f+T,10.),
   ro=-ri*0.121+.82;
   p.xy*=rot(T*.3+f);
   p.yz*=rot(T*.5+f+p.z*.2);
   p.xz*=rot(T*.7+f+p.x*.2);
   d=min(d,length(vec2(length(p.xz)-ri,p.y))-ro);
 }
  return d;
}
void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
  vec2 uv=vec2(fragCoord.x/iResolution.x,fragCoord.y/iResolution.y);
  uv-=.5;
  uv/=vec2(iResolution.y/iResolution.x,1);

  vec3 ro=vec3(0.,0.,-6.),
 rd=normalize(vec3(uv,1.)),//  
 mp=ro;
 float f,dmin=10.,dt=.2;
 for(f=0.;f<30.;f++){//        
   float d=map(mp);//          
   dmin=min(dmin,d);
   if(d<.01)break;
   mp+=rd*d;
 }
 float r=(dmin-.01)/(dt-.01);
 vec3 c=vec3(max(1.-f/30.,r*(1.-r)*4.));
 c=mix(c,vec3(0.9216, 0.0824, 0.0824),.29);
 vec4 out_color=vec4(1.);
  out_color = vec4(c,1.);
  fragColor=vec4(out_color);
}
