void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 hotspot = vec2(200.0, 200.0);
    float d = distance(vec2(iMouse.x, iMouse.y), fragCoord);
    float e = distance(vec2(iMouse.x, iMouse.y), hotspot);
    float eShift = 0.0;
    if(e < 100.0)
    {
        eShift = 1.0 - (e / 100.0);
    }
    if(d < 100.0)
    {
        float f = 1.0 - (d / 100.0);
        fragColor = vec4(f, f-eShift, f-eShift, f);
    }
}
