//Portfolio: https://www.artstation.com/angelologahd

//Based on code snippets from the shapes shapter in "The Book of Shaders":
//https://thebookofshaders.com/07/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
    vec2 st = fragCoord.xy / iResolution.xy;

    vec2 pos = vec2(0.5) - st;

    float r = length(pos) * 2.0;
    float a = atan(pos.y, pos.x) + cos(iTime * 0.2);
    float f = smoothstep(-0.5, 0.5, cos(a * 20.0)) * 0.2 + 0.5;

    vec3 color = texture(iChannel0, st).rgb * 0.5;
    color += texture(iChannel1, st).rgb * vec3(1.0 - smoothstep(f, f + 0.03, r), f * 0.85, f * 0.35);

    fragColor = vec4(color, 1.0);
}
