float rand2(vec2 id)
{
    return sin(dot(id, vec2(12.9898, 78.233)) * 43758.5453);
}

float circle(vec2 st, vec2 pos, float size){

    float d = distance(st, pos);
    d = step(d, size);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.0 - iResolution.xy)/ min(iResolution.x, iResolution.y);

    float n = 2.0;
    vec2 st = uv * n;
    st = fract(st);
    vec2 st2 = fract(st);
    //vec2 id = (floor(uv*n)+0.5)/n;
    vec2 id = (floor(st*n)+0.5)/n;

    vec3 col = vec3(0.0);
    float offs = rand2(uv);
    //float offs = rand(id)*60.;
    //float size = (1. + sin(iTime*3. + offs))*0.2;

    float c = circle(st, vec2(0.3, 0.7), 0.05);
    float c2 = circle(st, vec2(0.7, 0.7),0.05);
    float c3 = circle(st, vec2(0.5, 0.94), 0.06);

    st.x -= 0.5;
    st.y -= 0.5;
    st = abs(st);


    float d = dot(st, normalize(vec2(1.0, 1.0)));
    d = max(d-0.1, st.y);

    float d2 = dot(st, normalize(vec2(0.1, 0.2)));
    float s = step(d2, 0.1);


    //float d = distance(st, vec2(0.5, 0.5));
    d = step(0.4, d);

//    vec3 c1 = vec3(0.2, 0.5, 0.4);
//    vec3 c2 = vec3(0.9, 0.5, 0.3);
        vec3 c1 = vec3(0.9, 0.5, 0.2);
        vec3 c4 = vec3(0.5, 0.2, 0.4);

    col = vec3(mix(c1, c4, d));
    col *= mix(vec3(1., 1., 1.), vec3(0.3, 0.3, 0.3), s);
    col *= mix(vec3(1. ,1., 1.), vec3(0.3, 0.3, 0.3), c);
    col *= mix(vec3(1. ,1., 1.), vec3(0.3, 0.3, 0.3), c2);
    col *= mix(vec3(1., 1., 1.), vec3(0.3, 0.9, 0.4), c3);
    col *= mix(vec3(1., 1., 1.), vec3(0., 0., 0.),offs);

    //col += vec3(s, 0.1, 0.1);


    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}
