float getCircle(in vec2 uv, in vec2 pos, in float size) {

	vec2 coord = uv - pos;
    float d = length(coord);
    float blur = .0015;

    return smoothstep(length(coord)-blur, length(coord) + blur, size);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;
 	vec2 position = iMouse.xy/iResolution.xy;

    float r = .1;
  	float d1 = getCircle(uv, position * 1.25, .05);
    float d2 = getCircle(uv, position * .65, .1);
    float d3 = getCircle(uv, position, .01);

    float c = float(d1 + d2 + d3);
    fragColor = vec4(vec3(c),1.0);
}
