renderpass.code:
float k(float w, float o)
{
    return clamp((o+cos(iTime/2.)+cos(w*20.)/200.)*2.,-.1,1.1);
}

vec3 s(vec3 c, float t, float x, float w)
{
    float  p = 3./iResolution.y,
           f = smoothstep(0.+p,0.,x-t),
           l = smoothstep(0.+p,0.,x-t+w)-f;
    	   c=mix(c,1.-c,f)-l;
    return c;
}

void mainImage( out vec4 c, in vec2 f )
{
    vec2  u = f/iResolution.xy;
    float d = mod(floor(iTime/6.28),2.),
          x = d==0.?u.x:u.y,
       	  l = d==0.?u.y:u.x*2.,
	      o = d==0.?(u.y>.5?.6:0.):(u.x>.66?.9:(u.x<=.33?-.3:.3)),
          w = d==0.?.01:.02,
          t = k(l,o);
    bool  b = d==0.?u.y>.5:u.x<=.66&&u.x>.33;
    vec3  p = 0.5 + 0.5*cos(iTime+u.xyx+vec3(0,2,4));
		  c.rgb=s(p,b?x:t,b?(1.-t):x,w);
}
