#define sat(a) clamp(a,0.,1.)

mat2 r2d(float a){float cosa = cos(a);float sina = sin(a);return mat2(cosa,sina,-sina,cosa);}

float cir(vec2 p, float r)
{
  return length(p)-r;
}

vec3 rdr(vec2 uv)
{
  uv*=r2d(.1*iTime+length(uv)*.5);
  vec3 col=vec3(0.);

  float l = abs(sin(uv.y*30.+20.*sin(.5*iTime+uv.x*5.*length(uv*.2))))-2.3*(.1+.2*sin((uv.x+uv.y)*5.+iTime));

  col = mix(col,vec3(1.),1.-sat(l));
  return col;
}

vec3 rdr2(vec2 uv)
{
  float dist = (sin(-iTime*5.+(uv.x+uv.y)*5.)*.5+1.)*0.08;

  vec2 dir = normalize(vec2(1.));
  vec3 col;
  col.r = rdr(uv+dir*dist).r;
  col.g = rdr(uv).g;
  col.b = rdr(uv-dir*dist).b;
  return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv =( fragCoord.xy-0.5*iResolution.xy) / iResolution.xx;
  uv*=3.5;

  vec3 col = rdr2(uv);
  fragColor = vec4(col, 1.0);
}
