vec4 run(in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - vec2(0.5, 0.5);
    uv.x *= iResolution.x/iResolution.y;

   // Zoom    
    const float initialzoom = 60.0;
    float phase = 2.1;
    float zoom = 6.0 * pow(10.0, 1.0 + (cos(iTime*0.1505+phase) + 1.0) * 0.5 * 4.0);
    //zoom = 40.2;

    uv /= zoom*0.01;

    // Rotation
    float t = iTime*0.1;
    uv *= mat2(cos(t),sin(t), -sin(t),cos(t));

    // Translation
    //const vec2 focus = vec2(0.424513, 0.20753);  
	const vec2 focus = vec2(-0.045, 0.68059);    
    //const vec2 focus = vec2(0.142, -0.655);        

    //const vec2 focus = vec2(0.0);        

    uv += focus;     

    vec2 c = vec2(uv.x, uv.y);
    vec2 z = vec2(0.0, 0.0);

    bool draw = true;

    int nbiter = 0;    
    float distnce = 0.0;

       for(int i=0; i<100;i++) {

        float _2xy = 2.0*z.x*z.y;
        z.x = z.x*z.x - z.y*z.y;
        z.y = _2xy;

        z += c;

        distnce = length(z-c);           

        if(length(z)>2.0) {

			break;            
        }

        nbiter++;
    }

    float test = float(nbiter)/100.0 * (1.25 - pow(distnce* 0.5, 0.66));
    //    test = float(nbiter)/100.0 * (pow(distnce* 0.5, 0.66));

    return vec4(test);    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 fragColor1 = run(fragCoord + vec2(-0.25, -0.25));
    vec4 fragColor2 = run(fragCoord + vec2(+0.25, -0.25));
    vec4 fragColor3 = run(fragCoord + vec2(-0.25, +0.25));
    vec4 fragColor4 = run(fragCoord + vec2(+0.25, +0.25));

    fragColor = (fragColor1 + fragColor2 + fragColor3 + fragColor4) * 0.25;
}
