float circle(vec2 uv, vec2 p, float r, float blur) {
    uv -= p;
	float d = length(uv);
    float c = smoothstep(r+blur/2.0, r-blur/2.0, d);
    return c;
}

float band(float t, float start, float end, float blur) {
 	float side1 = smoothstep(start-blur/2.0, start+blur/2.0, t);
    float side2 = smoothstep(end+blur/2.0, end-blur/2.0, t);
    return side1 * side2;
}

float rect(vec2 uv, float left, float right, float bottom, float top, float blur) {
    float vband = band(uv.x, left, right, blur);
    float hband = band(uv.y, bottom, top, blur);
    return vband * hband;
}

float rect(vec2 uv, vec2 p, vec2 size, float blur) {
    float halfw = size.x/2.0;
    float halfv = size.y/2.0;
    return rect(uv, p.x - halfw, p.x + halfw, p.y - halfv, p.y + halfv, blur);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy; //0...1

    uv -= 0.5; // -0.5 ... 0.5
    uv.x *= iResolution.x/iResolution.y;

    //float c = circle(uv, vec2(0., 0.2), 0.2, 0.02);

    float r1 = rect(uv, vec2(0.0, -0.25), vec2(0.8, 0.2), 0.01);
    vec3 purple = vec3(r1) * vec3(0.4, 0.0, 0.8);


    float r2 = rect(uv, vec2(0.0, 0.05), vec2(0.4, 0.6), 0.01);
    vec3 yellow = vec3(r2) * vec3(0.9, 0.8, 0.0);

    float c1 = circle(uv, vec2(-0.4, 0.05), 0.3, 0.01);
    float c2 = circle(uv, vec2(0.4, 0.05), 0.3, 0.01);
    vec3 red = vec3(c1 + c2) * vec3(0.9, 0.1, 0.1);


	vec3 col = purple + yellow + red;

    // Output to screen
    fragColor = vec4(col,1.0);
}
