void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = texture(iChannel0, uv).rgb;

    /////////////////
    // Color shift //
    /////////////////
    float shift = 0.01;
    col = vec3(
        length(texture(iChannel0, uv).rgb)/sqrt(3.0),
        length(texture(iChannel0, uv).rgb)/sqrt(3.0),
        length(texture(iChannel0, uv + vec2(shift, 0.0)).rgb)/sqrt(3.0)
    );

    /////////////////
    //    Grain    //
    /////////////////
    float grain_power = 0.1;
    vec3 noise = texture(iChannel1, mod(uv + vec2(0, 10.0*iTime), 1.0)).rgb;
    col += grain_power * noise.r;

    fragColor = vec4(col,1.0);
}
