int julia(vec2 uv,  int iter, vec2 c) {
    vec2 z = uv;
    for (int i = 0; i < iter; i++) {
        if (dot(z, z) > 4.0) return i;
        z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + c;
    }
    return 0;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  int iter = int(120.*iMouse.x/iResolution.x);
  float zoom = 4.;


  //vec2 pos = vec2(-1.62167,0.0);
  vec2 pos = vec2(0.0,0.0);

    vec2 uv = pos + (fragCoord.xy*2.0 - iResolution.xy) * zoom/ iResolution.x;

  //  float ret = float(mandelbrot(uv,iter));
  // float ret = float(julia(uv,iter,vec2(-0.16,0.67)+vec2(0.07*sin(iTime*0.1),0.07*cos(iTime*0.09))));
     float ret = float(julia(uv,iter,vec2(-1.0,0.0)));
  fragColor = vec4(sin(vec3(0.1, 0.2, 0.5) * ret), 1);
}
