void mainImage(out vec4 Color,vec2 Coord)
{
    vec3 T = vec3(1,1,0)/iResolution;
    vec2 U = Coord*T.xy;

    Color = (texture(iChannel0,U)+texture(iChannel0,U+1.*T.xz)+
        texture(iChannel0,U+1.*T.zy)+texture(iChannel0,U+1.*T.xy))*.25;
}

vec2 mainSound( in int samp,float Time)
{
    return .1*Shape(Time);
}

vec2 Shape(float T)
{
    float N = fract(T)*440.;
    float Y = ceil(8.*fract(N)-4.5)/4.;
    vec2 S = 6.2831*vec2(Y,Y+1.)/4.;
    return vec2(0,Y)+cos(N)*sin(T+S)*vec2(.5,.2);
}

void mainImage(out vec4 Color, in vec2 Coord)
{
    vec2 U = (Coord*2.-iResolution.xy)/iResolution.y;

    float T = texture(iChannel0,Coord/256.+cos(iTime*vec2(8,9))).r;

    float D = 0.;
    for(float I = .0;I<1.;I+=.005)
    {
    	D = max(D,1.-length(Shape((T+I)*.1+iTime)-U)*40.);
    }

    vec3 C = D+T*vec3(.1,.1,.1+.05*dot(U,U));
    Color = vec4(C,1);
}
