#ifdef GL_ES
precision mediump float;
#endif


// Plot a line on Y using a value between 0.0-1.0
float plot(vec2 st, float pct){
  return  smoothstep( pct/0.1, pct, sin(st.y) * 2.0) -
          smoothstep( pct, pct+0.9, cos(st.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 st = gl_FragCoord.xy/iResolution.xy;

    float y = sin(st.x)*35.0;

    vec3 color = vec3(y);

    // Plot a line
    float pct = plot(st,cos(y) * sin(iTime));
    color = (0.1-pct)*color+pct*vec3(1.0, 0.5, 0.0);

	fragColor = vec4(color,1.0);
}
