const int RAY_STEPS = 256;
const float HALF_PI = 3.14159 * 0.5;

const vec3 greenSpherePos = vec3(-2.0, 0.0, 5.5);

#define GI 1

struct Intersection
{
    float t;
    vec3 color;
    vec3 p;
    int object;
};

vec3 rotateY(vec3 p, float a) {
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}

void raycast(vec2 uv, out vec3 dir, out vec3 eye, out vec3 ref) {
    eye = rotateY(vec3(0.0, 1.0, 15.0), 3.14159 * 1.5);
    ref = vec3(0.0, 0.0, 0.0);

    float len = tan(3.14159 * 0.125) * distance(eye, ref);
    vec3 H = normalize(cross(vec3(0.0, 1.0, 0.0), ref - eye));
    vec3 V = normalize(cross(H, eye - ref));
    V *= len;
    H *= len * iResolution.x / iResolution.y;
    vec3 p = ref + uv.x * H + uv.y * V;
    dir = normalize(p - eye);
}

float sphere(vec3 p, float r, vec3 c)
{
    return distance(p, c) - r;
}

float torus(vec3 p, vec2 t)
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q) - t.y;
}

float cone(vec3 p, vec2 c)
{
    // c must be normalized
    float q = length(p.xy);
    return dot(c,vec2(q,p.z));
}

// Box with side lengths b
float box(vec3 p, vec3 b)
{
  return length(max(abs(p) - b, 0.0));
}

float plane( vec3 p, vec4 n )
{
  // n must be normalized
  return dot(p,n.xyz) + n.w;
}

vec3 rotateX(vec3 p, float amt) {
    return vec3(p.x, cos(amt) * p.y - sin(amt) * p.z, sin(p.y) + cos(p.z));
}

float sceneMap3D(vec3 pos, vec3 lightPos)
{
    float t = sphere(pos, 4.0, vec3(0.0, 0.0, 0.0));
    t = min(t, sphere(pos, 2.0, greenSpherePos));
    t = min(t, box(pos + vec3(0.0, 3.0, 0.0), vec3(50.0, 1.0, 50.0)));
    return t;
}

float shadowMap3D(vec3 pos)
{
    float t = sphere(pos, 4.0, vec3(0.0, 0.0, 0.0));
    t = min(t, sphere(pos, 2.0, greenSpherePos));
    t = min(t, box(pos + vec3(0.0, 3.0, 0.0), vec3(50.0, 1.0, 50.0)));
    return t;
}

void sceneMap3D(vec3 pos, out float t, out int obj, vec3 lightPos)
{
    t = sphere(pos, 4.0, vec3(0.0, 0.0, 0.0));
    float t2;
    obj = 0; // 0 is center sphere
    if((t2 = sphere(pos, 2.0, greenSpherePos)) < t) {
        t = t2;
        obj = 1;
    }
    if((t2 = box(pos + vec3(0.0, 3.0, 0.0), vec3(50.0, 1.0, 50.0))) < t) {
        t = t2;
        obj = 3;
    }
}

void march(vec3 origin, vec3 dir, out float t, out int hitObj, vec3 lightPos)
{
    t = 0.001;
    for(int i = 0; i < RAY_STEPS; ++i)
    {
        vec3 pos = origin + t * dir;
    	float m;
        sceneMap3D(pos, m, hitObj, lightPos);
        if(m < 0.01)
        {
            return;
        }
        t += m;
    }
    t = -1.0;
    hitObj = -1;
}

vec3 computeNormal(vec3 pos, vec3 lightPos)
{
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize( vec3( sceneMap3D(pos + epsilon.yxx, lightPos) - sceneMap3D(pos - epsilon.yxx, lightPos),
                            sceneMap3D(pos + epsilon.xyx, lightPos) - sceneMap3D(pos - epsilon.xyx, lightPos),
                            sceneMap3D(pos + epsilon.xxy, lightPos) - sceneMap3D(pos - epsilon.xxy, lightPos)));
}

float softShadow(vec3 dir, vec3 origin, float min_t, float k) {
    float res = 1.0;
    float t = min_t;
    for(int i = 0; i < RAY_STEPS; ++i) {
        float m = shadowMap3D(origin + t * dir);
        if(m < 0.0001) {
            return 0.0;
        }
        res = min(res, k * m / t);
        t += m;
    }
    return res;
}

float shadow(vec3 dir, vec3 origin, float min_t) {
    return softShadow(dir, origin, min_t, 6.0);
}

vec3 computeMaterial(int hitObj, vec3 p, vec3 n, vec3 light, vec3 view) {
    float t;
    switch(hitObj) {
        case 0:
        // Red sphere
        vec3 color = vec3(1.0, 0.88, 0.7) * vec3(1.0, 0.67, 0.67) * max(0.0, dot(n, light)) * shadow(light, p, 0.1);
        #if GI
        color += 0.3 * vec3(1.0, 0.88, 0.7) * max(0.0, dot(n, normalize(greenSpherePos - p)));
        #endif
        return color;
        break;
        case 1:
        // Green sphere
        vec3 color2 = vec3(1.0, 0.88, 0.7) * vec3(1.0, 0.88, 0.7) * max(0.0, dot(n, light)) * shadow(light, p, 0.1);
        #if GI
        color2 += 0.3 * vec3(1.0, 0.67, 0.67) * max(0.0, dot(n, normalize(-p)));
        color2 += 0.5 * vec3(1.0, 0.88, 0.7) * max(0.0, 1.2 - p.y - 2.5);
        #endif
        return color2;
        break;
        case 3:
        // Floor
        t = floor(mod(1.0 * (sin(p.x) + sin(p.z)), 2.0));
        vec3 checker = mix(vec3(0.5), vec3(1.0), t);
        vec3 color3 = vec3(1.0, 0.88, 0.7) * checker * max(0.0, dot(n, light)) * shadow(light, p, 0.1);
        #if GI
        color3 += vec3(1.0, 0.67, 0.67) * smoothstep(0.2, 1.0, max(0.0, dot(n, normalize(-p))));
        color3 += 0.3 * vec3(1.0, 0.88, 0.7) * smoothstep(0.2, 1.0, max(0.0, dot(n, normalize(greenSpherePos-p))));
        #endif
        return color3;
        break;
        case -1:
        // Background
        return vec3(0.0);
        break;
    }
    return vec3(1.0);
}

Intersection sdf3D(vec3 dir, vec3 eye, vec3 lightPos)
{
    float t;
    int hitObj;
    march(eye, dir, t, hitObj, lightPos);

    vec3 isect = eye + t * dir;
    vec3 nor = computeNormal(isect, lightPos);
    vec3 lightDir = normalize(lightPos - isect);

    vec3 surfaceColor = computeMaterial(hitObj, isect, nor, lightDir, normalize(eye - isect));

    return Intersection(t, surfaceColor, isect, hitObj);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 lightPos = vec3(-12.0, 4.0, 0.0);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // [-1, 1]
    vec2 uv2 = 2.0 * uv - vec2(1.0);

    Intersection aaIsects[4];
    vec3 dir, eye, ref;
    int idx = 0;
    for(float i = 0.0; i < 1.0; i += 0.5) {
        for(float j = 0.0; j < 1.0; j += 0.5) {
            raycast(uv2 + vec2(i, j) / iResolution.xy, dir, eye, ref);
            aaIsects[idx++] = sdf3D(dir, eye, lightPos);
        }
    }
    vec3 avgColor = vec3(0.0);
    for(int i = 0; i < 4; ++i) {
        avgColor += aaIsects[i].color;
    }
    avgColor *= 0.25;
    fragColor = vec4(avgColor * step(0.0, aaIsects[0].t), 1.0);
    float fogT = smoothstep(30.0, 50.0, distance(aaIsects[0].p, eye));
    fragColor.rgb = mix(fragColor.rgb, vec3(0.0), fogT);
}
