void mainImage( out vec4 Q, in vec2 U )
{	vec2 R = iResolution.xy;
    vec2 d = vec2(
    	T(U+vec2(1,0)).x-T(U-vec2(1,0)).x,
    	T(U+vec2(0,1)).y-T(U-vec2(0,1)).y
    );
 	vec4 m = 0.25*(
